mapper.select:
SELECT
FROM AUTO_CASE WHERE DELETED = '0'
AND TESTCASE_NAME = #{testcaseName}
AND TESTCASE_NO != #{testcaseNo}

SELECT c.TESTCASE_NO as testcaseNo, c.DATA_ID as dataId, c.USER_ID as userId, c.FUNC_POINT as funcPoint, c.TESTCASE_NAME as testcaseName, c.PRECONDITION as precondition, c.TESTCASE_DESC as testcaseDesc, c.EXPECTED_RESULT as expectedResult, c.VALID_FLAG as validFlag, c.MENU_SHEET_ID as menuSheetId, c.ASSERT_ID as assertId, c.DELETED as deleted, c.CREATE_TIME as createTime, c.MODIFY_TIME as modifyTime, c.LAST_OPR as lastOpr, m.MENU_NO as menuNo, u.USERNAME as userName, d.LABEL as dataLabel, a.LABEL as assertLabel FROM AUTO_CASE c LEFT JOIN AUTO_MENU_SHEET m ON c.MENU_SHEET_ID = m.MENU_SHEET_ID LEFT JOIN AUTO_USER u ON u.USER_ID = c.USER_ID LEFT JOIN AUTO_CASE_DATA d ON d.DATA_ID = c.DATA_ID LEFT JOIN AUTO_ASSERT a ON a.ASSERT_ID = c.ASSERT_ID
AND c.TESTCASE_NAME like concat('%', #{search}, '%')
AND c.DELETED = #{valid}
AND c.VALID_FLAG = #{flag}
