mapper.select:
SELECT
FROM AUTO_ELEMENT WHERE DELETED = '0'
AND ELEMENT_NAME = #{elementName}
AND ELEMENT_ID != #{elementId}

mapper.update:
UPDATE AUTO_ELEMENT SET ELEMENT_TYPE = #{elementType}, ELEMENT_CONTENT = #{elementContent},
ELEMENT_NAME = #{elementName},
ELEMENT_DIR = #{elementDir}, MODIFY_TIME = #{time}, LAST_OPR = #{userNameEn} WHERE ELEMENT_ID = #{elementId}
