SELECT m.MODULE_NAME, d.MODULE_DETAIL_ID, d.MODULE_ID, d.ELEMENT_STEP_NO, d.ELEMENT_ID, d.ELEMENT_DYNAMIC_TYPE, d.ELEMENT_DATA, d.EXE_TIMES, d.EXE_TIMES, d.DELETED, d.CREATE_TIME, d.MODIFY_TIME, d.LAST_OPR, e.ELEMENT_NAME FROM AUTO_MODULE_DETAIL d LEFT JOIN AUTO_MODULE m ON m.MODULE_ID = d.MODULE_ID LEFT JOIN AUTO_ELEMENT e ON d.ELEMENT_ID = e.ELEMENT_ID
AND m.MODULE_NAME like concat('%', #{search}, '%')
AND d.DELETED = #{valid}

        SELECT
           
        FROM
            AUTO_MODULE_DETAIL
		WHERE 
			DELETED = '0'
            
                AND  MODULE_ID = #{moduleId}
            
            
                AND ELEMENT_STEP_NO = #{elementStepNo}
            
            
                AND MODULE_DETAIL_ID != #{moduleDetailId}

SELECT m.MODULE_NAME, d.MODULE_DETAIL_ID, d.MODULE_ID, d.ELEMENT_STEP_NO, d.ELEMENT_ID, d.ELEMENT_DYNAMIC_TYPE, d.ELEMENT_DATA, d.EXE_TIMES, d.EXE_TIMES, d.DELETED, d.CREATE_TIME, d.MODIFY_TIME, d.LAST_OPR, e.ELEMENT_NAME FROM AUTO_MODULE_DETAIL d LEFT JOIN AUTO_MODULE m ON m.MODULE_ID = d.MODULE_ID LEFT JOIN AUTO_ELEMENT e ON d.ELEMENT_ID = e.ELEMENT_ID WHERE d.DELETED = '0'
AND d.MODULE_ID = #{moduleId}
ORDER BY cast(d.ELEMENT_STEP_NO as unsigned integer)
