) tt group by id ) kk left join mantis_bug_table bt on kk.id = bt.id left join mantis_user_table ur on ur.id = bt.reporter_id left join mantis_user_table uh on uh.id = bt.handler_id left join mantis_project_table p on bt.project_id = p.id left join mantis_bug_text_table tp on bt.id = tp.id left join (select a.id as id, (b.fixTime-a.openTime) as solveTime from (select distinct id, date_submitted as openTime from mantis_bug_table where id in (select id from mantis_bug_table where id in (select bt.id from mantis_bug_table bt, mantis_bug_history_table h where bt.id = h.bug_id and h.field_name='status' and h.new_value = 90) and id not in (select bt.id from mantis_bug_table bt, mantis_bug_history_table h where bt.id = h.bug_id and h.field_name='status' and h.new_value = 40)) ) a join (select distinct bug_id, max(date_modified) as fixTime from mantis_bug_history_table where field_name = 'status' and new_value = 80 and bug_id in (select id from mantis_bug_table where id in (select bt.id from mantis_bug_table bt, mantis_bug_history_table h where bt.id = h.bug_id and h.field_name='status' and h.new_value = 90) and id not in (select bt.id from mantis_bug_table bt, mantis_bug_history_table h where bt.id = h.bug_id and h.field_name='status' and h.new_value = 40)) group by bug_id) b on a.id = b.bug_id) st on kk.id = st.id left join (select bt.id, ifnull(count(bt.id),0) as num from mantis_bug_table bt left join mantis_bug_history_table h on bt.id = h.bug_id where h.new_value = '50' group by bt.id ) wn on wn.id = kk.id
and ur.username = #{reporter}
and uh.username = #{handler}
and wn.num = 1 and bt.status = 90
and wn.num >=2
