le bt left join mantis_bug_history_table h on bt.id = h.bug_id where h.new_value = '50' group by bt.id ) wn on wn.id = kk.id order by kk.id desc

SELECT workNo, sum(if(find_in_set('需规问题', i.issue_flaw),1,0)) as rqrRuleNum, sum(if(find_in_set('功能实现不完整', i.issue_flaw),1,0)) as funcLackNum, sum(if(find_in_set('功能实现错误', i.issue_flaw),1,0)) as funcErrNum, sum(if(find_in_set('业务需求问题', i.issue_flaw),1,0)) as rqrNum, sum(if(find_in_set('历史遗留问题', i.issue_flaw),1,0)) as historyNum, sum(if(find_in_set('优化建议', i.issue_flaw),1,0)) as optimizeNum, sum(if(find_in_set('后台问题', i.issue_flaw),1,0)) as backNum, sum(if(find_in_set('打包问题', i.issue_flaw),1,0)) as packageNum, sum(if(find_in_set('兼容性异常', i.issue_flaw),1,0)) as compatibilityNum, sum(if(find_in_set('数据问题', i.issue_flaw),1,0)) as dataNum, sum(if(find_in_set('环境问题', i.issue_flaw),1,0)) as envNum, sum(if(find_in_set('其他原因', i.issue_flaw),1,0)) as otherNum, sum(if(i.status in (30,40,90),1,0)) as solveIssue, sum(if(i.status in (10,20,50,80),1,0)) as unSolveIssue, group_concat(distinct i.developer_cn) as developer_cn FROM ( SELECT kk.id, kk.workNo, kk.issue_flaw, bt.status, kk.developer_cn, kk.groupId FROM ( select * from ( SELECT tt.id, max(case field_id when 13 then value else '' end) as workNo, max(case field_id when 5 then value else '' end) as issue_flaw, max(case field_id when 8 then value else '' end) as developer_cn, max(case field_id when 73 then value else '' end) as groupId FROM( SELECT b.id, p.field_id, s.value FROM mantis_bug_table b LEFT JOIN mantis_custom_field_project_table p ON p.project_id = b.project_id LEFT JOIN mantis_custom_field_string_table s ON ( s.field_id = p.field_id AND b.id = s.bug_id ) where b.id in ( select id from mantis_bug_table where env = #{env}
and from_unixtime(date_submitted,'%Y-%m-%d') between #{startDate} and #{endDate}
) ) tt GROUP BY id ) d where d.workNo is not null and d.workNo !=''
and d.groupId in
#{group}
and d.workNo in (${workNos})
) kk LEFT JOIN mantis_bug_table bt ON kk.id = bt.id ) i GROUP BY workNo

SELECT o.id, o.fdevGroupId, replace(left(o.createTime, 10),'-','') as createTime, ifnull(p.num, 1) as openTimes, o.priority, o.severity, ifnull(q.solveTime, 0) as solveTime, o.developer, o.taskNo, o.summary, o.status, o.workNo, u.realname as reporterName, uu.realname as handlerName , ifnull(if(o.status=90,replace(left(from_unixtime(m.date_modified),10),'-',''),null), 0) as closeTime FROM ( SELECT kk.id, kk.fdevGroupId, from_unixtime(date_submitted) as createTime, kk.developer, kk.taskNo,kk.workNo, bt.priority, bt.severity, bt.summary, bt.status, bt.reporter_id, bt.handler_id FROM ( SELECT tt.id, max(case field_id when 73 then value else '' end) as fdevGroupId, max(case field_id when 5 then value else '' end) as issue_flaw, max(case field_id when 8 then value else '' end) as developer, max(case field_id when 19 then value else '' end) as taskNo , max(case field_id when 13 then value else '' end) as workNo FROM( SELECT b.id, p.field_id, s.value FROM mantis_bug_table b LEFT JOIN mantis_custom_field_project_table p ON p.project_id = b.project_id LEFT JOIN mantis_custom_field_string_table s ON ( s.field_id = p.field_id AND b.id = s.bug_id ) where s.value != ''
and b.env = #{env}
) tt GROUP BY id ) kk LEFT JOIN mantis_bug_table bt ON kk.id = bt.id LEFT JOIN mantis_project_table p ON bt.project_id = p.id where kk.fdevGroupId != '' and kk.issue_flaw NOT IN ('数据问题','环境问题','其他原因') ) o LEFT JOIN ( SELECT b.id, b.fdevGroupId, b.num FROM ( SELECT count(a.id) as num, a.id, a.fdevGroupId FROM ( SELECT g.id, g.fdevGroupId FROM ( SELECT kk.id, kk.fdevGroupId FROM ( SELECT tt.id, max(case field_id when 73 then value else '' end) as fdevGroupId FROM ( SELECT b.id, p.field_id, s.value FROM mantis_bug_table b LEFT JOIN mantis_custom_field_project_table p ON p.project_id = b.project_id LEFT JOIN mantis_custom_field_string_table s ON ( s.field_id = p.field_id AND b.id = s.bug_id ) WHERE s.value != ''
and b.env = #{env}
) tt GROUP BY id ) kk LEFT JOIN mantis_bug_table bt ON kk.id = bt.id LEFT JOIN mantis_project_table p ON bt.project_id = p.id where kk.fdevGroupId != '' ) g LEFT JOIN mantis_bug_history_table h ON g.id = h.bug_id WHERE h.field_name = 'status' AND h.new_value = '50' ) a GROUP BY a.id, a.fdevGroupId ) b WHERE b.num !=1 ) p ON o.id = p.id LEFT JOIN ( select a.id as id, (b.fixTime-a.openTime) as solveTime from ( select distinct id, date_submitted as openTime from mantis_bug_table where id in ( select id from mantis_bug_table where id in ( select bt.id from mantis_bug_table bt, mantis_bug_history_table h where bt.id = h.bug_id and h.field_name='status' and h.new_value = 90 ) and id not in ( select bt.id from mantis_bug_table bt, mantis_bug_history_table h where bt.id = h.bug_id and h.field_name='status' and h.new_value = 40 ) ) ) a join ( select distinct bug_id, max(date_modified) as fixTime from mantis_bug_history_table where field_name = 'status' and new_value = 80 and bug_id in ( select id from mantis_bug_table where id in ( select bt.id from mantis_bug_table bt, mantis_bug_history_table h where bt.id = h.bug_id and h.field_name='status' and h.new_value = 90 ) and id not in ( select bt.id from mantis_bug_table bt, mantis_bug_history_table h where bt.id = h.bug_id and h.field_name='status' and h.new_value = 40 ) ) group by bug_id ) b on a.id = b.bug_id ) q ON o.id = q.id left join mantis_user_table u on o.reporter_id = u.id left join mantis_user_table uu on o.handler_id = uu.id left join (select distinct bug_id, date_modified from mantis_bug_history_table where new_value = 90) m on m.bug_id=o.id

